package com.diegolozano.walletapp;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;

public class ListAccountActivity extends AppCompatActivity {

    private ArrayList<Account> listAccountRV = new ArrayList<>();

    private RecyclerView myRecycleAccount;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_list_account);
        loadFakeData();

        myRecycleAccount=findViewById(R.id.rv_list_account);
        AccountAdapter myAdaptador = new AccountAdapter(listAccountRV);
        myRecycleAccount.setAdapter(myAdaptador);
        myRecycleAccount.setLayoutManager(new LinearLayoutManager(ListAccountActivity.this));
    }

    private void loadFakeData(){
        Account myAccount1= new Account("Bancolombia","Cuenta de ahorros",1003402.00,"data:image/png;base64,");
        Account myAccount2= new Account("Davivienda","Cuenta debito",100354445.00,"data:image/png;base64,");
        Account myAccount3= new Account("Efecto","Efectivo",1000000.0,"https://e7.pngegg.com/pngimages/472/645/png-clipart-money-bag-cash-coin-credit-card-cash-coupon-template-payment-cash.png");

        listAccountRV.add(myAccount1);
        listAccountRV.add(myAccount2);
        listAccountRV.add(myAccount3);

    }
}